import { View, Text, TextInput, Platform, TouchableOpacity, KeyboardAvoidingView } from 'react-native'
import React, { useState } from 'react';
import styles from './style';

const Form = (props) => {
    const [task, setTask] = useState('')
    const handleAddTask = () => {
        // setNumber(2);
        if(task.length === 0)
        {
            alert('vui lòng nhập công việc');
            return false;
        } else {
            alert(task);
        }
    }

    return (
        <KeyboardAvoidingView behavior={Platform.OS === 'ios' ? 'padding' : 'height'} keyboardVerticalOffset={10} style={styles.addTask}>
        <TextInput onChangeText={(text => setTask(text))} placeholder='Your Task' style={styles.input}/>
        <TouchableOpacity onPress={handleAddTask}>
            <View style={styles.iconWrapper}>
                <Text style={styles.icon}>+</Text>
            </View>
        </TouchableOpacity>
        </KeyboardAvoidingView>
    )
}

export default Form
